  //     code: !!dataKor ? dataKor.code : null,
  //     img: !!dataKor ? dataKor.img : null,
  //     name: !!dataKor ? dataKor.name_eng : null,
  //     price: !!dataKor ? data : null,
  //     isSnkrDunkOk: isSnkrDunkOk,
